import { Box, BoxProps, Text } from "@chakra-ui/react";
import { motion, SVGMotionProps } from "framer-motion";
import React, { useState } from "react";

import { navigate } from "gatsby";

import { chakraMotionWrapper } from "gatsby-theme-ekkus-design-library";
import useGlobal from "../store";

type NavigationTreeProps = Omit<BoxProps, "transitionDelay"> & {
  transitionDelay?: number;
};

const MotionBox = chakraMotionWrapper(Box);
const MotionText = chakraMotionWrapper(Text);

type TreeLinkProps = SVGMotionProps<SVGTextElement> & {
  to: string;
  duration: number;
  delay: number;
  children: React.ReactChild | JSX.Element;
};

const TreeLink = ({
  to,
  duration,
  delay,
  children,
  ...textProps
}: TreeLinkProps) => (
  <MotionText
    as={motion.text}
    fontSize="4xl"
    onClick={() => navigate(to)}
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    transition={{
      delay,
      duration,
    }}
    {...textProps}
    cursor="pointer"
  >
    {children}
  </MotionText>
);

